import React, { useState } from "react";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { Link, useNavigate } from "react-router-dom";
import bg from "../images/bg_img.jpg";
import "./UserLoginForm.css";
import { GoogleOAuthProvider } from "@react-oauth/google";
import { GoogleLogin } from "@react-oauth/google";
import jwt_decode from "jwt-decode";
import axios from "axios";

const UserLoginForm = () => {
  let navigate = useNavigate();

  const [loginRequest, setLoginRequest] = useState({
    emailId: "",
    password: "",
    role: "Customer",
  });

  const handleUserInput = (e) => {
    setLoginRequest({ ...loginRequest, [e.target.name]: e.target.value });
  };

  const loginAction = (e) => {
    e.preventDefault();
    if (!e.target.checkValidity()) {
      toast.error("Please fill in all the required fields", {
        position: "top-center",
        autoClose: 2000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
      });
      return;
    }

    fetch("http://localhost:8080/api/user/login", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(loginRequest),
    })
      .then((result) => {
        console.log("result", result);
        result
          .json()
          .then((res) => {
            console.log(res);

            if (res && res.role === "Admin") {
              console.log("Working fine:)");
              sessionStorage.setItem("active-admin", JSON.stringify(res));
            } else if (res && res.role === "Customer") {
              sessionStorage.setItem("active-user", JSON.stringify(res));
            } else if (res && res.role === "Delivery") {
              sessionStorage.setItem("active-delivery", JSON.stringify(res));
            } else {
              // Handle the case when res is null or the role property is missing
              toast.error("Invalid email and password", {
                position: "top-center",
                autoClose: 2000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
              });
              return;
            }

            toast.success("logged in successfully!!!", {
              position: "top-center",
              autoClose: 1000,
              hideProgressBar: false,
              closeOnClick: true,
              pauseOnHover: true,
              draggable: true,
              progress: undefined,
            });

            navigate("/home");
            window.location.reload(true);
          })
          .catch((error) => {
            console.error(error);
            // Handle the error here, such as displaying an error message to the user
            toast.error("Invalid email and password", {
              position: "top-center",
              autoClose: 2000,
              hideProgressBar: false,
              closeOnClick: true,
              pauseOnHover: true,
              draggable: true,
              progress: undefined,
            });
          });
      })
      .catch((error) => {
        console.error(error);
        // Handle the error here, such as displaying an error message to the user
        toast.error("Failed to connect to the server", {
          position: "top-center",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
        });
      });
  };

  const [user, setUser] = useState({
    firstName: "",
    lastName: "",
    emailId: "",
    password: "",
    phoneNo: "",
    street: "",
    city: "",
    pincode: "",
    role: "",
  });

  const addUser = async (user) => {
    const res = await axios.post(
      "http://localhost:8080/api/user/register",
      user
    );
    console.log(res.data);
    return res.data;
  };

  const loginWithGoogle = (details) => {
    user.firstName = details.given_name;
    user.lastName = details.family_name;
    user.emailId = details.email;
    user.password = "";
    user.role = "Customer";
    setUser(user);

    axios
      .get("http://localhost:8080/api/user/email/" + user.emailId)
      .then((res) => {
        if (res.data) {
          sessionStorage.setItem("active-user", JSON.stringify(res.data));
          navigate("/home");
          window.location.reload(true);
        } else {
          addUser(user)
            .then((res) => {
              sessionStorage.setItem("active-user", JSON.stringify(res));
              navigate("/home");
              window.location.reload(true);
            })
            .catch((error) => {
              console.error(error);
              // Handle the error here, such as displaying an error message to the user
              toast.error("Failed to log in via Google. Please try again.", {
                position: "top-center",
                autoClose: 2000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
              });
            });
        }
      });
  };

  use(() => {
    const forms = document.querySelectorAll('.needs-validation')

    Array.from(forms).forEach(form => {
      form.addEventListener('submit', event => {
        if (!form.checkValidity()) {
          event.preventDefault()
          event.stopPropagation()
        }

        form.classList.add('was-validated')
      }, false)
    })
  }, [])
  return (
    <>
      <div className="login-img">
        <div className="vertical-down container">
          <div className="mt-2 d-flex aligns-items-center justify-content-center">
            <div className="card" style={{ width: "25rem", marginLeft: "-52rem", marginTop: "3.7rem" }}>
              <div className="card-header">
                <h4 className="card-title" style={{ marginLeft: "7.6rem", fontStyle: "italic" }}>
                  <b>User Login</b>
                </h4>
              </div>
              <div className="card-body">
                <form className="needs-validation" onSubmit={loginAction} noValidate>
                  <div className="mb-3">
                    <div className="form-floating">
                      <input
                        type="email"
                        className="form-control"
                        id="emailId"
                        name="emailId"
                        placeholder="Email ID"
                        onChange={handleUserInput}
                        value={loginRequest.emailId}
                        required
                        style={{ marginTop: "1rem", height: "3rem" }}
                      />
                      <label htmlFor="emailId">Email ID *</label>
                      <div className="invalid-feedback">Please provide a valid email.</div>
                    </div>
                  </div>

                  <div className="mb-3">
                    <div className="form-floating">
                      <input
                        type="password"
                        className="form-control"
                        id="password"
                        name="password"
                        placeholder="Password"
                        onChange={handleUserInput}
                        value={loginRequest.password}
                        autoComplete="on"
                        required
                        style={{ marginTop: "0.5rem", height: "3rem" }}
                      />
                      <label htmlFor="password">Password *</label>
                      <div className="invalid-feedback">Please provide a password.</div>
                    </div>
                  </div>

                  <div className="d-flex justify-content-between align-items-center">
                    <button
                      type="submit"
                      className="btn bg-color custom-bg-text"
                      style={{ marginTop: "1.5rem", marginLeft: "9.2rem" }}
                    >
                      Login
                    </button>
                  </div>
                  <div className="line">
                    <hr class="l1" style={{ width: "9rem", height: "1px", backgroundColor: "white", marginTop: "1.6rem", marginLeft: "1rem" }} />
                    <h6 style={{ marginLeft: "10.5rem", marginTop: "-1.7rem", fontStyle: "italic" }}>OR</h6>
                    <hr class="l2" style={{ width: "9rem", marginLeft: "12.3rem", marginTop: "-1.05rem" }} />
                  </div>
                  <div className="login-gmail">
                    <button style={{ marginTop: "1rem", marginLeft: "6rem", borderRadius: "2rem" }}>
                      <GoogleOAuthProvider clientId="668976964137-tljnqvmeh5jq54u9ldevost06cn878pl.apps.googleusercontent.com">
                        <GoogleLogin
                          onSuccess={(credentialResponse) => {
                            const details = jwt_decode(credentialResponse.credential);
                            console.log("", "detail of user", details);
                            loginWithGoogle(details);
                          }}
                          onError={() => {
                            console.log("Login Failed");
                          }}
                        >
                          Login Via Google
                        </GoogleLogin>
                      </GoogleOAuthProvider>
                    </button>
                  </div>
                  <ToastContainer />
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default UserLoginForm;
