import React from "react";
import { useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import { useEffect } from "react";

const UpdateStock = () => {
  const productId = useParams();

  const [product, setProduct] = useState({
    id: "",
    title: "",
    description: "",
    quantity: "",
    price: "",
    imageName: "",
    category: { id: "", title: "" },
  });

  const retrieveProduct = async () => {
    const response = await axios.get(
      "http://localhost:8080/api/product/" + productId
    );

    return response.data;
  };

  useEffect(() => {
    const getProduct = async () => {
      const retrievedProduct = await retrieveProduct();

      setProduct(retrievedProduct);
    };

    getProduct();
  }, []);

  return (
    <div>
      <div
        className="card form-card mt-1 ms-2 me-2 mb-2 card-color border-color"
        style={{
          height: "35rem",
        }}
      >
        <div
          className="card-body"
          style={{
            overflowY: "auto",
          }}
        >
          <form class="row g-3"></form>
          <div className="table-responsive">
            <table className="table table-hover text-center">
              <thead className="table-bordered border-color custom-bg text-color">
                <tr>
                  
                  <th scope="col">Title</th>
                  <th scope="col">Quantity</th>
                  <th scope="col">Quantity</th>
                  <th scope="col"> Price</th>
                 
                </tr>
              </thead>
              <tbody class="text-color">
                {product.map(() => {
                  return (
                    <tr>
                      <td>
                        <b>{product.title}</b>
                      </td>
                      <td>
                        <img
                          src={
                            "http://localhost:8080/api/product/" +
                            product.imageName
                          }
                          class="img-fluid"
                          alt="product_pic"
                          style={{
                            maxWidth: "90px",
                          }}
                        />
                      </td>
                      <td>
                        <b>{product.quantity}</b>
                      </td>
                      <td>
                        <b>{product.price}</b>
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UpdateStock;
