    const productId=useParams()


    const [product, setProduct] = useState({
        id: "",
        title: "",
        description: "",
        quantity: "",
        price: "",
        imageName: "",
        category: { id: "", title: "" },
      });
      
      const retrieveProduct = async () => {
        const response = await axios.get(
          "http://localhost:8080/api/product/"+productId
        );
    
        return response.data;
      };

