import React, { useState, useEffect } from "react";
import axios from "axios";

const MyProfile = () => {
  const user = JSON.parse(sessionStorage.getItem("active-user"));

  const [profile, setProfile] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const getMyProfile = async () => {
      try {
        const profileResult = await retrieveUser();
        setProfile(profileResult);
      } catch (error) {
        setError(error);
      } finally {
        setIsLoading(false);
      }
    };

    getMyProfile();
  }, []);

  const retrieveUser = async () => {
    try {
      const response = await axios.get(
        `http://localhost:8080/api/user/profile/${user.id}`
      );
      return response.data;
    } catch (error) {
      throw new Error("Failed to retrieve user profile");
    }
  };

  const handlePhoneNo = (e) => {
    setProfile((prevProfile) => ({
      ...prevProfile,
      phoneNo: e.target.value,
    }));
  };

  const handleWallet = (e) => {
    setProfile((prevProfile) => ({
      ...prevProfile,
      walletAmount: e.target.value,
    }));
  };

  const handleAddress = (e) => {
    setProfile((prevProfile) => ({
      ...prevProfile,
      address: {
        ...prevProfile.address,
        street: e.target.value,
      },
    }));
  };

  const handlePincode = (e) => {
    setProfile((prevProfile) => ({
      ...prevProfile,
      address: {
        ...prevProfile.address,
        pincode: e.target.value,
      },
    }));
  };

  const handleCity = (e) => {
    setProfile((prevProfile) => ({
      ...prevProfile,
      address: {
        ...prevProfile.address,
        city: e.target.value,
      },
    }));
  };

  const handleSaveButton = async () => {
    try {
      const response = await axios.post(
        `http://localhost:8080/api/user/profile/${user.id}`,
        profile
      );
      console.log(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  if (isLoading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error.message}</div>;
  }

  return (
    <div className="gd container">
      <div style={{ marginTop: "60px" }}>
        <div className="card" style={{ borderRadius: "10px" }}>
          <div className="card-header custom-bg">
            <h2>Profile Page</h2>
          </div>
          <div className="card-body">
            <form className="row g-3">
              <div className="col-md-6">
                <label htmlFor="firstName" className="form-label">
                  First Name
                </label>
                <input
                  type="text"
                  className="form-control"
                  id="firstName"
                  value={profile.firstName}
                  onChange={handleFirstName}
                  style={{ background: "#e8ecef" }}
                />
              </div>
              <div className="col-md-6">
                <label htmlFor="inputPassword4" className="form-label">
                  Last Name
                </label>
                <input
                  type="text"
                  className="form-control"
                  id="inputPassword4"
                  value={profile.lastName}
                  onChange={handleLastName}
                  style={{ background: "#e8ecef" }}
                />
              </div>
              {/* Other form fields */}
              <div className="col-12">
                <div className="d-flex justify-content-end">
                  <button
                    type="submit"
                    className="btn bg-color btn-primary"
                    onClick={handleSaveButton}
                  >
                    Save
                  </button>
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MyProfile;
