import React, { useEffect, useState } from "react";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { Link, useNavigate } from "react-router-dom";
import bg from "../images/bg_img.jpg";
import "./UserLoginForm.css";
import { GoogleOAuthProvider } from "@react-oauth/google";
import { GoogleLogin } from "@react-oauth/google";
import jwt_decode from "jwt-decode";
import axios from "axios";
import bgt from "../images/nam_bg.png";

const UserLoginForm = () => {
  let navigate = useNavigate();

  const [loginRequest, setLoginRequest] = useState({
    emailId: "",
    password: "",
    role: "Customer",
  });

  const handleUserInput = (e) => {
    setLoginRequest({ ...loginRequest, [e.target.name]: e.target.value });
  };

  const validateForm = (event) => {
    const form = document.getElementById("addUserForm");
    if (form.checkValidity() === false) {
      event.preventDefault();
      event.stopPropagation();
    }
    form.classList.add("was-validated");
  };

  const loginAction = (e) => {
    e.preventDefault();
    if (!e.target.checkValidity()) {
      toast.error("Please fill in all the required fields", {
        position: "top-center",
        autoClose: 2000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
      });
      return;
    }

    fetch("http://localhost:8080/api/user/login", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(loginRequest),
    })
      .then((result) => {
        console.log("result", result);
        result
          .json()
          .then((res) => {
            console.log(res);

            if (res && res.role === "Admin") {
              console.log("Working fine:)");
              sessionStorage.setItem("active-admin", JSON.stringify(res));
            } else if (res && res.role === "Customer") {
              sessionStorage.setItem("active-user", JSON.stringify(res));
            } else if (res && res.role === "Delivery") {
              sessionStorage.setItem("active-delivery", JSON.stringify(res));
            } else {
              toast.error("Invalid email and password", {
                position: "top-center",
                autoClose: 2000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
              });
              return;
            }

            toast.success("logged in successfully!!!", {
              position: "top-center",
              autoClose: 1000,
              hideProgressBar: false,
              closeOnClick: true,
              pauseOnHover: true,
              draggable: true,
              progress: undefined,
            });

            navigate("/home");
            window.location.reload(true);
          })
          .catch((error) => {
            console.error(error);
            toast.error("Invalid email and password", {
              position: "top-center",
              autoClose: 2000,
              hideProgressBar: false,
              closeOnClick: true,
              pauseOnHover: true,
              draggable: true,
              progress: undefined,
            });
          });
      })
      .catch((error) => {
        console.error(error);
        toast.error("Failed to connect to the server", {
          position: "top-center",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
        });
      });
  };

  const [user, setUser] = useState({
    firstName: "",
    lastName: "",
    emailId: "",
    password: "",
    phoneNo: "",
    street: "",
    city: "",
    pincode: "",
    role: "",
  });

  const addUser = async (user) => {
    const res = await axios.post(
      "http://localhost:8080/api/user/register",
      user
    );
    console.log(res.data);
    return res.data;
  };

  const loginWithGoogle = (details) => {
    user.firstName = details.given_name;
    user.lastName = details.family_name;
    user.emailId = details.email;
    user.password = "";
    user.role = "Customer";
    setUser(user);

    axios
      .get("http://localhost:8080/api/user/email/" + user.emailId)
      .then((res) => {
        if (res.data) {
          sessionStorage.setItem("active-user", JSON.stringify(res.data));
          navigate("/home");
          window.location.reload(true);
        } else {
          addUser(user)
            .then((res) => {
              sessionStorage.setItem("active-user", JSON.stringify(res));
              navigate("/home");
              window.location.reload(true);
            })
            .catch((error) => {
              console.error(error);
              toast.error("Failed to log in via Google. Please try again.", {
                position: "top-center",
                autoClose: 2000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
              });
            });
        }
      });
  };

  return (
    <>
      <div className="vertical-down container">
        <div className="mt-2 d-flex aligns-items-center justify-content-center">
          <div
            className="card"
            style={{ width: "25rem", marginLeft: "-52rem", marginTop: "3.7rem", background: "#FFFFFF" }}
          >
            <div className="card-header">
              <h4 className="card-title" style={{ marginLeft: "7.6rem", fontStyle: "italic" }}>
                <b>User Login</b>
              </h4>
            </div>
            <div className="card">
              <form
                className="needs-validation"
                onSubmit={loginAction}
                id="addUserForm"
                noValidate
              >
                <div className="mb-3">
                  <div className="form-floating">
                    <input
                      type="email"
                      className="form-control"
                      id="emailId"
                      name="emailId"
                      placeholder="Email ID"
                      onChange={handleUserInput}
                      value={loginRequest.emailId}
                      required
                      style={{ marginTop: "1rem", height: "3rem" }}
                    />
                    <label htmlFor="emailId">Email ID *</label>
                    <div className="invalid-feedback">Please provide a valid email.</div>
                  </div>
                </div>

                <div className="mb-3">
                  <div className="form-floating">
                    <input
                      type="password"
                      className="form-control"
                      id="password"
                      name="password"
                      placeholder="Password"
                      onChange={handleUserInput}
                      value={loginRequest.password}
                      autoComplete="on"
                      required
                      style={{ marginTop: "0.5rem", height: "3rem" }}
                    />
                    <label htmlFor="password">Password *</label>
                    <div className="invalid-feedback">Please provide a password.</div>
                  </div>
                </div>

                <div className="mb-3">
                  <button
                    type="submit"
                    className="btn btn-primary btn-block"
                    style={{ marginTop: "1.5rem", marginBottom: "1rem" }}
                  >
                    Login
                  </button>
                </div>
              </form>
            </div>
            <div
              className="card-footer"
              style={{
                background: "#F3F2F2",
                borderTop: "none",
                borderBottomLeftRadius: "1rem",
                borderBottomRightRadius: "1rem",
              }}
            >
              <h6 style={{ textAlign: "center" }}>
                Don't have an account?{" "}
                <Link to="/register" style={{ color: "#007BFF" }}>
                  Register here
                </Link>
              </h6>
              <div className="text-center">
                <GoogleLogin
                  clientId="your-client-id"
                  onSuccess={loginWithGoogle}
                  onFailure={loginWithGoogle}
                  cookiePolicy={"single_host_origin"}
                  responseType="code,token"
                  scope="https://www.googleapis.com/auth/userinfo.email https://www.googleapis.com/auth/userinfo.profile"
                >
                  <i className="fab fa-google" style={{ fontSize: "2rem", marginRight: "0.5rem" }}></i>
                  <span style={{ fontSize: "1.2rem" }}>Continue with Google</span>
                </GoogleLogin>
              </div>
            </div>
          </div>
        </div>
      </div>
      <ToastContainer />
    </>
  );
};

export default UserLoginForm;
