import React, { useState } from "react";


const SearchBox = () => {
 

  return (
    <div className="search-container">
      <form className="search-form" onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Search"
          value={searchText}
          onChange={handleSearchInputChange}
        />

        <select
          className="category-filter"
          value={categoryFilter}
          onChange={handleCategoryFilterChange}
        >
          <option value="" disabled selected>
            Filter by Category
          </option>
          <option value="aloo bhujia">Aloo Bhujia</option>
          <option value="khatta meetha">Khatta Meetha</option>
          <option value="moong daal">Moong Daal</option>
          <option value="namkeens">Namkeens</option>
          <option value="navrattan">Navrattan</option>
          <option value="nut cracker">Nut Cracker</option>
          <option value="punjabi mix">Punjabi Mix</option>
          <option value="bhel mix">Bhel Mix</option>
          <option value="gathiya">Gathiya</option>
          <option value="sev">Sev</option>
        </select>

        <button type="submit">Search</button>
      </form>

      {/* Display product suggestions */}
      {showSuggestions && (
        <ul className="suggestions-list">
          {getProductSuggestions().map((suggestion) => (
            <li
              key={suggestion}
              onClick={() => handleSuggestionClick(suggestion)}
            >
              {suggestion}
            </li>
          ))}
        </ul>
      )}

      {/* Render search results */}
      {/* Implement your search result rendering logic here */}
    </div>
  );
};

export default SearchPage;
