import { useState } from "react";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { useNavigate } from "react-router-dom";

const AdminLoginPage = () => {
  let navigate = useNavigate();

  const [loginRequest, setLoginRequest] = useState({
    emailId: "",
    password: "",
    role: "Admin",
  });

  const [response, setResponse] = useState({});

  const handleUserInput = (e) => {
    setLoginRequest({ ...loginRequest, [e.target.name]: e.target.value });
  };

  const loginAction = (e) => {
    fetch("http://localhost:8080/api/user/admin/login", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(loginRequest),
    }).then((result) => {
      console.log("RESULT", result);
      result
        .json()
        .then((res) => {
          if (res==null) {
            // Handle the case when res is null
            toast.error("Wrong Email Id or Password, Try Again", {
              position: "top-center",
              autoClose: 2000,
              hideProgressBar: false,
              closeOnClick: true,
              pauseOnHover: true,
              draggable: true,
              progress: undefined,
            });
            return;
          }
          else{
          sessionStorage.setItem("active-admin", JSON.stringify(res));
          console.log("Working fine:)");
  
          toast.success("logged in successfully!!!", {
            position: "top-center",
            autoClose: 1000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
          });
  
          // navigate("/home");
          // window.location.reload(true);
        }
        })
        .catch((error) => {
          console.error(error);
          // Handle the error here, such as displaying an error message to the user
          toast.error("Wrong Email Id or Password, Try Again", {
            position: "top-center",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
          });
        });
    });
  
    e.preventDefault();
  };
  

  return (
    <div>
      <div className="mt-2 d-flex aligns-items-center justify-content-center">
        <div
          className="card vertical-down card-color form-card border-color"
          style={{ width: "35rem" }}
        >
          <div className="card-header custom-bg  text-center bg-color-text">
            <h4 className="card-title">Admin Login</h4>
          </div>
          <div className="card-body">
            <form  onSubmit={loginAction}>
              <div className="mb-3">
                <div className="form-floating">
                  <input
                    type="email"
                    className="form-control"
                    id="emailId"
                    name="emailId"
                    placeholder="Email Id"
                    onChange={handleUserInput}
                    // value={loginRequest.emailId}
                    required
                  />
                  <label htmlFor="emailId">Email Id</label>
                </div>
              </div>
              <div className="mb-3">
                <div className="form-floating">
                  <input
                    type="password"
                    className="form-control"
                    id="password"
                    name="password"
                    placeholder="Password"
                    onChange={handleUserInput}
                    // value={loginRequest.password}
                    autoComplete="on"
                    required
                  />
                  <label htmlFor="password">Password</label>
                </div>
              </div>
              
              <button
                type="submit"
                className="btn bg-color custom-bg-text"
                // onClick={loginAction}
              >
                Login
              </button>
              <ToastContainer />
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminLoginPage;
