import React, { useState, useEffect } from 'react';
import axios from 'axios';

const MyProfile = () => {
  const [profile, setProfile] = useState({
    firstName: "",
    lastName: "",
    email: "",
  });

  useEffect(() => {
    const getMyProfile = async () => {
      try {
        const profileResult = await retrieveUser();
        if (profileResult) {
          console.log("My profile is present");
          setProfile(profileResult);
        }
      } catch (error) {
        console.log("Error fetching profile:", error);
      }
    };

    getMyProfile();
  }, []);

  const retrieveUser = async () => {
    try {
      const response = await axios.get("http://localhost:8080/api/user/profile/2");
      console.log(response.data);
      return response.data;
    } catch (error) {
      console.log("Error retrieving user profile:", error);
      return null;
    }
  };

  const handleProfileInput = (e) => {
    setProfile({ ...profile, [e.target.name]: e.target.value });
  };

  return (
    <div className="container">
      <div className="vertical-down">
        <form className="row g-3">
          <div>
            <h2>Profile Page</h2>
          </div>
          <div className="col-md-6">
            <label htmlFor="inputFirstName" className="form-label">
              First Name
            </label>
            <input
              type="text"
              className="form-control"
              id="inputFirstName"
              name="firstName"
              value={profile.firstName}
              onChange={handleProfileInput}
            />
          </div>
          <div className="col-md-6">
            <label htmlFor="inputLastName" className="form-label">
              Last Name
            </label>
            <input
              type="text"
              className="form-control"
              id="inputLastName"
              name="lastName"
              value={profile.lastName}
              onChange={handleProfileInput}
            />
          </div>
          <div className="col-12">
            <label htmlFor="inputEmail" className="form-label">
              Email Id
            </label>
            <input
              type="email"
              className="form-control"
              id="inputEmail"
              name="email"
              value={profile.email}
              onChange={handleProfileInput}
            />
          </div>
          {/* Additional input fields for address, city, state, zip code */}
          {/* ... */}
          <div className="col-12" style={{ marginTop: "60px" }}>
            <button type="submit" className="btn btn-primary" onClick={""}>
              Save
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default MyProfile;
