import React, { useState } from "react";
import "react-toastify/dist/ReactToastify.css";
import { ToastContainer, toast } from "react-toastify";

const UserLoginPage = () => {
  const [user, setUser] = useState({
    emailId: "",
    password: "",
  });

  const handleUserInput = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const loginUser = (event) => {
    event.preventDefault();
    // Login logic goes here
    // ...
  };

  const validateForm = (event) => {
    const form = document.getElementById("loginForm");
    if (form.checkValidity() === false) {
      event.preventDefault();
      event.stopPropagation();
    }
    form.classList.add("was-validated");
  };

  return (
    <>
      <div className="img">
        <div className="mt-2 d-flex aligns-items-center justify-content-center ms-2 me-2 mb-2">
          <div
            className="card"
            style={{ width: "30rem", marginLeft: "39rem", marginTop: "3.5rem" }}
          >
            <div className="card-header">
              <h5 className="card-title" style={{ marginLeft: "12rem", fontStyle: "italic" }}>
                <b>Login</b>
              </h5>
            </div>
            <div className="card-body">
              <form id="loginForm" className="needs-validation" onSubmit={loginUser} noValidate>
                <div className="form-floating mb-3 text-color">
                  <input
                    type="email"
                    className="form-control"
                    id="floatingEmailId"
                    name="emailId"
                    placeholder="Email Id"
                    onChange={handleUserInput}
                    required
                  />
                  <label htmlFor="floatingEmailId">Email Id *</label>
                  <div className="invalid-feedback">Please enter a valid email address.</div>
                </div>

                <div className="form-floating mb-3 mt-1 text-color">
                  <input
                    type="password"
                    className="form-control"
                    id="floatingPassword"
                    name="password"
                    placeholder="Password"
                    onChange={handleUserInput}
                    required
                  />
                  <label htmlFor="floatingPassword">Password *</label>
                  <div className="invalid-feedback">Please enter a password.</div>
                </div>

                <input
                  type="submit"
                  className="btn bg-color custom-bg-text"
                  value="Login"
                  style={{ marginLeft: "12rem" }}
                  onClick={validateForm}
                />

                <ToastContainer />
              </form>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default UserLoginPage;
