import React, { useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios"; // Import axios for making API requests

const NormalHeader = () => {
  const [keyword, setKeyword] = useState(""); // State variable for search keyword
  const [searchResults, setSearchResults] = useState([]); // State variable for search results

  const handleSearch = async (e) => {
    e.preventDefault(); // Prevent the form from submitting and refreshing the page

    try {
      const response = await axios.get(`http://localhost:8080/api/product/search/${keyword}`); // Make a GET request to the backend API
      setSearchResults(response.data); // Update the search results state with the fetched data
      console.log("hello",response.data)
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <ul className="navbar-nav ms-auto mb-2 mb-lg-0 me-5">
      <li>
      <div class="input-group rounded">
      <input type="search" class="form-control rounded" 
     placeholder="Search" aria-label="Search" aria-describedby="search-addon" onClick={handleSerch} />

      </div>
      </li>
      {/* Render the search results */}
      {searchResults.map((product) => (
        <li className="nav-item" key={product.id}>
          <Link to={`/products/${product.id}`} className="nav-link active" aria-current="page">
            <b className="text-color">{product.name}</b>
          </Link>
        </li>
      ))}

      <li className="nav-item">
        <Link to="/user/register" className="nav-link active" aria-current="page">
          <b className="text-color">Register</b>
        </Link>
      </li>

      <li className="nav-item">
        <Link to="/user/login" className="nav-link active" aria-current="page">
          <b className="text-color">Login </b>
        </Link>
      </li>
    </ul>
  );
};
export default NormalHeader;
