import { useState } from "react";
import "react-toastify/dist/ReactToastify.css";
import { ToastContainer, toast } from "react-toastify";

const AddUserForm = () => {
  const [user, setUser] = useState({
    firstName: "",
    lastName: "",
    emailId: "",
    password: "",
    phoneNo: "",
    street: "",
    city: "",
    pincode: "",
    role: "",
  });

  const handleUserInput = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const saveUser = (event) => {
    event.preventDefault();
    fetch("http://localhost:8080/api/user/register", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(user),
    }).then((result) => {
      console.log("******near toast thing");
      toast.success("Registered Successfully!!!", {
        position: "top-center",
        autoClose: 1000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
      });
      console.warn("result", result);
      result
        .json()
        .then((res) => {
          console.log("response", res);
        })
        .catch((error) => {
          console.log("******", error);
          console.log(error);
        });
    });
  };

  return (
    <div class="mt-2 d-flex align-items-center justify-content-center">
  <div class="card form-card border-color text-color custom-bg" style="max-width: 55rem;">
    <div class="card-header custom-bg bg-color-text text-center">
      <h5 class="card-title">Add User</h5>
    </div>
    <div class="card-body">
      <form onSubmit={saveUser}>
        <div class="mb-3">
          <label for="role" class="form-label"><strong>User Role</strong></label>
          <select class="form-control" name="role" onChange={handleUserInput}>
            <option value="0">Select Role</option>
            <option value="Admin">Admin</option>
            <option value="Customer">Customer</option>
            <option value="Delivery">Delivery Person</option>
          </select>
        </div>

        <div class="mb-3">
          <label for="firstName" class="form-label"><strong>First Name</strong></label>
          <input type="text" class="form-control" id="firstName" name="firstName" onChange={handleUserInput} value={user.firstName} />
        </div>

        <div class="mb-3">
          <label for="lastName" class="form-label"><strong>Last Name</strong></label>
          <input type="text" class="form-control" id="lastName" name="lastName" onChange={handleUserInput} value={user.lastName} />
        </div>

        <div class="mb-3">
          <label class="form-label"><strong>Email Id</strong></label>
          <input type="email" class="form-control" id="emailId" name="emailId" onChange={handleUserInput} value={user.emailId} />
        </div>

        <div class="mb-3">
          <label for="password" class="form-label"><strong>Password</strong></label>
          <input type="password" class="form-control" id="password" name="password" onChange={handleUserInput} value={user.password} />
        </div>

        <div class="mb-3">
          <label for="phoneNo" class="form-label"><strong>Mobile No</strong></label>
          <input type="number" class="form-control" id="phoneNo" name="phoneNo" onChange={handleUserInput} value={user.phoneNo} />
        </div>

        <div class="mb-3">
          <label for="street" class="form-label"><strong>Street</strong></label>
          <textarea class="form-control" id="street" name="street" rows="3" onChange={handleUserInput} value={user.street}></textarea>
        </div>

        <div class="mb-3">
          <label for="city" class="form-label"><strong>City</strong></label>
          <input type="text" class="form-control" id="city" name="city" onChange={handleUserInput} value={user.city} />
        </div>

        <div class="mb-3">
          <label for="pincode" class="form-label"><strong>Pincode</strong></label>
          <input type="number" class="form-control" id="pincode" name="pincode" onChange={handleUserInput} value={user.pincode} />
        </div>

        <input type="submit" class="btn bg-color custom-bg-text" value="Register User" />

        <ToastContainer />
      </form>
    </div>
  </div>
</div>

  );
};

export default AddUserForm;
