import { useState } from "react";
import "react-toastify/dist/ReactToastify.css";
import { ToastContainer, toast } from "react-toastify";

const AddUserForm = () => {
  const [user, setUser] = useState({
    firstName: "",
    lastName: "",
    emailId: "",
    password: "",
    phoneNo: "",
    street: "",
    city: "",
    pincode: "",
    role: "",
  });

  const handleUserInput = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const saveUser = (event) => {
    event.preventDefault();
    fetch("http://localhost:8080/api/user/register", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(user),
    }).then((result) => {
      console.log("******near toast thing");
      toast.success("Registered Successfully!!!", {
        position: "top-center",
        autoClose: 1000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
      });
      console.warn("result", result);
      result
        .json()
        .then((res) => {
          console.log("response", res);
        })
        .catch((error) => {
          console.log("******", error);
          console.log(error);
        });
    });
  };

  return (
    <div>
      <div className="mt-2 d-flex aligns-items-center justify-content-center ms-2 me-2 mb-2">
        <div
          className="card form-card border-color text-color card-color"
          style={{ width: "35rem" }}
        >
          <div className="card-header custom-bg bg-color-text text-center">
            <h5 className="card-title">Add User</h5>
          </div>
          <div className="card-body">
            <form onSubmit={saveUser}>
              <div className="form-floating mb-3 text-color">
                <select
                  onChange={handleUserInput}
                  className="form-select"
                  name="role"
                  id="floatingRole"
                  value={user.role}
                  required
                >
                  <option value="0">Select Role</option>
                  <option value="Admin"> Admin </option>
                  <option value="Customer"> Customer </option>
                  <option value="Delivery"> Delivery Person </option>
                </select>
                <label htmlFor="floatingRole">User Role</label>
              </div>

                    <div class="form-floating">
                  <input type="password" class="form-control" id="floatingPassword" placeholder="Password"/>
                  <label for="floatingPassword">Password</label>
                </div>

              <div className="form-floating">
                <input
                  type="text"
                  className="form-control"
                  id="floatingFirstName"
                  name="firstName"
                  placeholder=""
                  onChange={handleUserInput}
                  
                  required
                />
                <label htmlFor="floatingFirstName">First Name</label>
              </div>

              <div className="form-floating mb-3 text-color">
                <input
                  type="text"
                  className="form-control"
                  id="floatingLastName"
                  name="lastName"
                  onChange={handleUserInput}
                  value={user.lastName}
                  required
                />
                <label htmlFor="floatingLastName">Last Name</label>
              </div>

              <div className="form-floating mb-3 text-color">
                <input
                  type="email"
                  className="form-control"
                  id="floatingEmailId"
                  name="emailId"
                  onChange={handleUserInput}
                  value={user.emailId}
                  required
                />
                <label htmlFor="floatingEmailId">Email Id</label>
              </div>

              <div className="form-floating mb-3 mt-1 text-color">
                <input
                  type="password"
                  className="form-control"
                  id="floatingPassword"
                  name="password"
                  onChange={handleUserInput}
                  value={user.password}
                  required
                />
                <label htmlFor="floatingPassword">Password</label>
              </div>

              <div className="form-floating mb-3">
                <input
                  type="number"
                  className="form-control"
                  id="floatingPhoneNo"
                  name="phoneNo"
                  onChange={handleUserInput}
                  value={user.phoneNo}
                  required
                />
                <label htmlFor="floatingPhoneNo">Mobile No</label>
              </div>

              <div className="form-floating mb-3">
                <textarea
                  className="form-control"
                  id="floatingStreet"
                  name="street"
                  rows="3"
                  onChange={handleUserInput}
                  value={user.street}
                ></textarea>
                <label htmlFor="floatingStreet">Street</label>
              </div>

              <div className="form-floating mb-3">
                <input
                  type="text"
                  className="form-control"
                  id="floatingCity"
                  name="city"
                  onChange={handleUserInput}
                  value={user.city}
                />
                <label htmlFor="floatingCity">City</label>
              </div>

              <div className="form-floating mb-3">
                <input
                  type="number"
                  className="form-control"
                  id="floatingPincode"
                  name="pincode"
                  onChange={handleUserInput}
                  value={user.pincode}
                />
                <label htmlFor="floatingPincode">Pincode</label>
              </div>

              <input
                type="submit"
                className="btn bg-color custom-bg-text"
                value="Register User"
              />

              <ToastContainer />
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddUserForm;
